import React, { useState, useEffect } from 'react';
import { getTransactions } from '../../services/TransactionService/transactionService';
import './TransactionHistory.css';

const TransactionHistory = () => {
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [filter, setFilter] = useState({
    startDate: '',
    endDate: '',
    minAmount: '',
    maxAmount: '',
    productType: ''
  });
  const [searchTerm, setSearchTerm] = useState('');
  const [isFilterVisible, setIsFilterVisible] = useState(false);

  useEffect(() => {
    loadTransactions();
  }, []);

  const loadTransactions = async () => {
    try {
      setLoading(true);
      const data = await getTransactions();
      setTransactions(data);
      setError('');
    } catch (err) {
      setError('Failed to load transactions: ' + err.message);
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilter({
      ...filter,
      [name]: value
    });
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const applyFilter = () => {
    // In a real app, this would call the API with filter parameters
    // For now, we'll just simulate filtering on the client side
    loadTransactions();
  };

  const resetFilter = () => {
    setFilter({
      startDate: '',
      endDate: '',
      minAmount: '',
      maxAmount: '',
      productType: ''
    });
    loadTransactions();
  };

  const toggleFilter = () => {
    setIsFilterVisible(!isFilterVisible);
  };

  // Filter and search transactions
  const filteredTransactions = transactions.filter(transaction => {
    // Search functionality
    if (searchTerm && !transaction.customer_name.toLowerCase().includes(searchTerm.toLowerCase()) &&
        !transaction.transaction_id.toLowerCase().includes(searchTerm.toLowerCase())) {
      return false;
    }

    // Apply filters
    if (filter.startDate && new Date(transaction.transaction_date) < new Date(filter.startDate)) {
      return false;
    }
    if (filter.endDate && new Date(transaction.transaction_date) > new Date(filter.endDate)) {
      return false;
    }
    if (filter.minAmount && parseFloat(transaction.total_amount) < parseFloat(filter.minAmount)) {
      return false;
    }
    if (filter.maxAmount && parseFloat(transaction.total_amount) > parseFloat(filter.maxAmount)) {
      return false;
    }
    if (filter.productType && transaction.product_type !== filter.productType) {
      return false;
    }

    return true;
  });

  return (
    <div className="transaction-history">
      <h1>Transaction History</h1>

      <div className="transaction-controls">
        <div className="search-container">
          <input
            type="text"
            placeholder="Search by customer or transaction ID..."
            value={searchTerm}
            onChange={handleSearchChange}
            className="search-input"
          />
        </div>

        <div className="filter-toggle">
          <button 
            className="btn btn-outline-secondary btn-sm"
            onClick={toggleFilter}
          >
            {isFilterVisible ? 'Hide Filters' : 'Show Filters'}
          </button>
        </div>
      </div>

      {isFilterVisible && (
        <div className="filter-section">
          <div className="filter-row">
            <div className="filter-group">
              <label>Start Date</label>
              <input
                type="date"
                name="startDate"
                value={filter.startDate}
                onChange={handleFilterChange}
                className="form-control form-control-sm"
              />
            </div>

            <div className="filter-group">
              <label>End Date</label>
              <input
                type="date"
                name="endDate"
                value={filter.endDate}
                onChange={handleFilterChange}
                className="form-control form-control-sm"
              />
            </div>

            <div className="filter-group">
              <label>Min Amount</label>
              <input
                type="number"
                name="minAmount"
                value={filter.minAmount}
                onChange={handleFilterChange}
                placeholder="0.00"
                className="form-control form-control-sm"
              />
            </div>

            <div className="filter-group">
              <label>Max Amount</label>
              <input
                type="number"
                name="maxAmount"
                value={filter.maxAmount}
                onChange={handleFilterChange}
                placeholder="0.00"
                className="form-control form-control-sm"
              />
            </div>

            <div className="filter-group">
              <label>Product Type</label>
              <select
                name="productType"
                value={filter.productType}
                onChange={handleFilterChange}
                className="form-control form-control-sm"
              >
                <option value="">All Types</option>
                <option value="Digital Software">Digital Software</option>
                <option value="E-book">E-book</option>
                <option value="Digital Media">Digital Media</option>
                <option value="Online Service">Online Service</option>
              </select>
            </div>
          </div>

          <div className="filter-actions">
            <button 
              className="btn btn-primary btn-sm"
              onClick={applyFilter}
            >
              Apply Filters
            </button>
            <button 
              className="btn btn-secondary btn-sm"
              onClick={resetFilter}
            >
              Reset
            </button>
          </div>
        </div>
      )}

      {error && <div className="alert alert-danger">{error}</div>}

      {loading ? (
        <div className="loading">Loading transactions...</div>
      ) : (
        <div className="transaction-table-container">
          <table className="transaction-table">
            <thead>
              <tr>
                <th>Transaction ID</th>
                <th>Date</th>
                <th>Customer</th>
                <th>Product</th>
                <th>Amount</th>
                <th>Tax</th>
                <th>Total</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredTransactions.length === 0 ? (
                <tr>
                  <td colSpan="8" className="no-results">No transactions found</td>
                </tr>
              ) : (
                filteredTransactions.map(transaction => (
                  <tr key={transaction.id}>
                    <td>{transaction.transaction_id}</td>
                    <td>{new Date(transaction.transaction_date).toLocaleDateString()}</td>
                    <td>{transaction.customer_name}</td>
                    <td>{transaction.product_type}</td>
                    <td>${parseFloat(transaction.subtotal).toFixed(2)}</td>
                    <td>${parseFloat(transaction.tax_amount).toFixed(2)}</td>
                    <td>${parseFloat(transaction.total_amount).toFixed(2)}</td>
                    <td>
                      <div className="action-buttons">
                        <button 
                          className="btn btn-primary btn-sm" 
                          onClick={() => alert(`View transaction ${transaction.id}`)}
                        >
                          View
                        </button>
                        <button 
                          className="btn btn-danger btn-sm" 
                          onClick={() => alert(`Delete transaction ${transaction.id}`)}
                        >
                          Delete
                        </button>
                      </div>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default TransactionHistory;
